import { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { ChatContext } from '../context/ChatContext';

const Message = ({ message }) => {
  const { currentUser } = useContext(AuthContext);
  const { data } = useContext(ChatContext);
  console.log(message);
  return (
    <div className={`message ${message.senderId === currentUser.uid}`}>
      <div className="messageInfo">
        <img
          src={
            message.senderId === currentUser.uid ? currentUser.photoURL : data.user.photoURL
          }></img>
        <span>just now</span>
      </div>
      <div className="messageContent">
        <p>Hello!</p>
        <img
          src="https://images.pexels.com/photos/9921261/pexels-photo-9921261.jpeg?auto=compress&cs=tinysrgb&w=800&lazy=load"
          alt=""
        />
      </div>
    </div>
  );
};

export default Message;
